mapper.update:
UPDATE board
post_title = #{postTitle} , post_content = #{postContent} , market_code = #{marketCode:NUMERIC}, price = #{price:NUMERIC}, city = #{city:VARCHAR}, phone = #{phone:VARCHAR}, qna_code = #{qnaCode:CHAR}, route = #{route:VARCHAR}, prod_name = #{prodName:VARCHAR}
WHERE post_no = #{postNo}

   	UPDATE board 

		view_count	= view_count + 1
   	
   	WHERE post_no = #{postNo}

   	UPDATE board

		recommend_count	= recommend_count + 1
   	
   	WHERE post_no = #{postNo}

mapper.select:
SELECT COUNT(*) FROM( SELECT post_no, board_code, id, nickname, post_title, post_content, reg_date, view_count, recommend_count, market_code, price, city , phone, qna_code, route, prod_name, del_code FROM board
board_code = #{boardCode} AND del_code != '0'
) countTable
