mapper.update:
UPDATE mileage
set mileage= mileage + #{mileage}
WHERE id=#{id}

mapper.select:
SELECT * FROM ( SELECT inner_table.* , ROWNUM AS row_seq FROM ( SELECT mileage_no, id, mileage, country, prod_detail, company, discount_price FROM product
AND p.prod_no like '%' || #{searchKeyword} ||'%'
AND p.prod_name like '%' || #{searchKeyword} ||'%'
AND p.price like '%' || #{searchKeyword} ||'%'
) inner_table WHERE ROWNUM <= #{endRowNum} ) WHERE row_seq BETWEEN #{startRowNum} AND #{endRowNum}
