mapper.update:
UPDATE users
email=#{email:VARCHAR}, phone=#{phone:NUMERIC}, user_addr=#{userAddr:VARCHAR}, account=#{account:VARCHAR}, birth_date=#{birthDate:NUMERIC}, gender=#{gender:CHAR}, levels=#{levels}, level_point=#{levelPoint}
WHERE id=#{id}

UPDATE users

	access_date=SYSDATE,
	access_ip=#{accessIp}

WHERE id=#{id}

UPDATE users
id=#{id}, pw=#{pw}, nickname=#{nickname}, email=#{email:VARCHAR}, phone=#{phone:NUMERIC}, user_addr=#{userAddr:VARCHAR}, birth_date=#{birthDate:NUMERIC}, profile=#{profile:VARCHAR}
WHERE id=#{id}

mapper.select:
SELECT id, user_name, nickname, levels
FROM( SELECT inner_table.*, ROWNUM AS row_seq
	FROM( SELECT id, user_name, nickname, levels
		FROM users
		
			
				
					id=#{searchKeyword}
				
				
					user_name=#{searchKeyword}
				
				
					nickname=#{searchKeyword}
				
				
					level=#{searchKeyword}
				
			
		
		ORDER BY id ) inner_table
	WHERE ROWNUM <=#{endRowNum} )
WHERE row_seq BETWEEN #{startRowNum} AND #{endRowNum}

SELECT COUNT(*)
FROM(	SELECT id, user_name, nickname, levels
	FROM users
	
		
			
				id=#{searchKeyword}
			
			
				user_name=#{searchKeyword}
			
			
				nickname=#{searchKeyword}
			
			
				level=#{searchKeyword}
			
		
	 ) countTable
